INSERT INTO `questions` VALUES (1,1,'Đơn vị đo góc được gọi là gì?',NULL,NULL,'easy'),(2,1,'Kí hiệu của \"độ\" là gì?',NULL,NULL,'easy'),(3,1,'\"Sáu mươi độ\" được viết như thế nào?',NULL,NULL,'easy'),(4,1,'Dụng cụ dùng để đo góc là gì?',NULL,NULL,'easy'),(5,1,'Khi đo góc, chúng ta phải đặt tâm của thước đo góc trùng với đâu?',NULL,NULL,'easy'),(6,1,'Khi đo góc, một cạnh của góc phải nằm trên đường gì của thước?',NULL,NULL,'easy'),(7,1,'Nếu cạnh OA đi qua vạch số 60 trên thước, số đo góc là bao nhiêu?',NULL,NULL,'easy'),(8,1,'Thước đo góc có hình dạng giống hình gì?',NULL,NULL,'easy'),(9,1,'Thao tác đầu tiên khi đo góc là gì?',NULL,NULL,'easy'),(10,1,'Sau khi đặt thước đúng, muốn biết số đo góc ta làm gì?',NULL,NULL,'easy'),(11,2,'Câu nối tương ứng',NULL,NULL,'easy'),(12,3,'Theo em, có mấy bước đó góc?',NULL,NULL,'easy'),(13,3,'Khi đo góc, nếu cạnh OA đi qua vạch số 45, số đo góc là bao nhiêu?',NULL,NULL,'easy'),(14,3,'Một góc có số đo là 75 độ. Em hãy viết kí hiệu số đo góc đó.',NULL,NULL,'easy'),(15,3,'“Sáu mươi độ\" được viết như thế nào?',NULL,NULL,'easy'),(16,3,'Dụng cụ nào dùng để đo góc?',NULL,NULL,'easy'),(17,4,'Góc nào bé hơn góc vuông?',NULL,NULL,'easy'),(18,4,'Góc nào lớn hơn góc vuông?',NULL,NULL,'easy'),(19,4,'Góc bẹt bằng bao nhiêu góc vuông?',NULL,NULL,'easy'),(20,4,'Góc có đỉnh O, cạnh OA, OB là góc nhọn. Phát biểu nào đúng?',NULL,NULL,'easy'),(21,4,'Góc nào có hai cạnh là hai tia đối nhau?',NULL,NULL,'easy'),(22,4,'Góc tù đỉnh O có cạnh OM, ON. So với góc vuông, góc này thế nào?',NULL,NULL,'easy'),(23,4,'Trong các góc sau, góc nào không phải là góc bẹt?',NULL,NULL,'easy'),(24,4,'Góc nhọn là góc như thế nào?',NULL,NULL,'easy'),(25,4,'Góc bẹt đỉnh O có cạnh OC, OD có đặc điểm gì?',NULL,NULL,'easy'),(26,4,'Góc tù so với góc nhọn thì thế nào?',NULL,NULL,'easy'),(27,5,'Câu nối tương ứng',NULL,NULL,'easy'),(28,6,'Quan sát các đồ vật xung quanh, trong các đồ vật sau, đồ vật nào có góc nhọn: Mép bàn học, mũi kéo, góc thước kẻ.',NULL,NULL,'easy'),(29,6,'Tron